import React, { useState, useEffect } from 'react';
import './LoadingBar.css';

const LoadingBar = ({ isVisible, onComplete }) => {
  const [progress, setProgress] = useState(0);
  const [currentStep, setCurrentStep] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);

  const steps = [
    { name: 'Uploading screenshot...', duration: 800 },
    { name: 'Analyzing visual elements...', duration: 1000 },
    { name: 'Extracting text content...', duration: 800 },
    { name: 'Generating CRO ideas...', duration: 2000 },
    { name: 'Calculating ICE scores...', duration: 800 },
    { name: 'Finalizing results...', duration: 600 }
  ];

  useEffect(() => {
    if (!isVisible) {
      setProgress(0);
      setCurrentStep(0);
      setIsAnimating(false);
      return;
    }

    setIsAnimating(true);
    let startTime = Date.now();

    const totalDuration = steps.reduce((sum, step) => sum + step.duration, 0);

    const updateProgress = () => {
      const elapsed = Date.now() - startTime;
      const progressPercent = Math.min((elapsed / totalDuration) * 95, 95);

      setProgress(progressPercent);

      // Update current step based on elapsed time
      let cumulativeTime = 0;
      for (let i = 0; i < steps.length; i++) {
        cumulativeTime += steps[i].duration;
        if (elapsed < cumulativeTime) {
          setCurrentStep(i);
          break;
        }
      }

      if (elapsed < totalDuration && isAnimating) {
        requestAnimationFrame(updateProgress);
      } else if (elapsed >= totalDuration) {
        setProgress(95);
        setCurrentStep(steps.length - 1);

        // If analysis is complete, wait a moment then go to 100%
        setTimeout(() => {
          setProgress(100);
          // Hide the loading bar after reaching 100%
          setTimeout(() => {
            setIsAnimating(false);
          }, 500);
        }, 200);
      }
    };

    requestAnimationFrame(updateProgress);
  }, [isVisible, isAnimating]);

  // If analysis completes while loading bar is still animating, let it finish
  useEffect(() => {
    if (!isVisible && isAnimating && progress < 95) {
      // Analysis completed early, but let the loading bar finish its animation
      return;
    }
  }, [isVisible, isAnimating, progress]);

  // Don't hide the loading bar if it's still animating
  if (!isVisible && !isAnimating) return null;

  return (
    <div className="loading-container">
      <div className="loading-content">
        <h3>ðŸ”„ Analyzing Your Screenshot</h3>
        <p className="current-step">{steps[currentStep]?.name || 'Processing...'}</p>

        <div className="progress-bar-container">
          <div 
            className="progress-bar" 
            style={{ width: `${progress}%` }}
          ></div>
        </div>

        <div className="progress-text">
          {Math.round(progress)}% Complete
        </div>

        <div className="loading-tips">
          <p>ðŸ’¡ <strong>Tip:</strong> This usually takes about 6 seconds to analyze your page</p>
          <p>ðŸ“Š We're analyzing your page for conversion optimization opportunities</p>
        </div>
      </div>
    </div>
  );
};

export default LoadingBar;
